CREATE TABLE "Activite" ("id_activite" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "nom"  NOT NULL , "description" );
CREATE TABLE "Adresse" ("id_adresse" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "nom_rue" VARCHAR NOT NULL , "numero1" INTEGER NOT NULL , "numero2" VARCHAR, "ville" VARCHAR, "c_postal" INTEGER, "c_pays" INTEGER, "d_creation" DATETIME NOT NULL  DEFAULT CURRENT_TIME, "d_maj" DATETIME);
CREATE TABLE "Avis" ("id_avis" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "id_up" INTEGER, "id_service" INTEGER, "id_avis_p" INTEGER, "d_creation" DATETIME NOT NULL  DEFAULT CURRENT_TIME, "cmt" TEXT NOT NULL  DEFAULT CURRENT_TIME);
CREATE TABLE "Contact" ("id_contact" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "prenom" VARCHAR NOT NULL , "nom" VARCHAR NOT NULL , "num_tel" INTEGER NOT NULL , "email" VARCHAR NOT NULL , "d_creation" DATETIME NOT NULL  DEFAULT CURRENT_TIME, "d_maj" DATETIME);
CREATE TABLE "Favoris" ("id_up" INTEGER NOT NULL , "id_service" INTEGER NOT NULL , "d_creation" DATETIME NOT NULL  DEFAULT CURRENT_TIME, PRIMARY KEY ("id_up", "id_service"));
CREATE TABLE "Hist_pos" ("id_up" INTEGER NOT NULL , "d_creation" DATETIME NOT NULL  DEFAULT CURRENT_TIME, "lat" DOUBLE NOT NULL , "lon" DOUBLE NOT NULL );
CREATE TABLE "Note" ("id_up"  NOT NULL , "id_service"  NOT NULL , "note" INTEGER NOT NULL , "d_creation" DATETIME NOT NULL  DEFAULT CURRENT_TIME, PRIMARY KEY ("id_up", "id_service"));
CREATE TABLE "Prix_service" ("id_prix" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "id_service" INTEGER NOT NULL , "d_deb" DATETIME NOT NULL , "d_fin" DATETIME NOT NULL , "min_pers" INTEGER NOT NULL , "max_pers" INTEGER NOT NULL , "prix_unitaire" DOUBLE NOT NULL , "promo" BOOL NOT NULL  DEFAULT 0, "valid" BOOL NOT NULL  DEFAULT 1, "d_creation" DATETIME NOT NULL  DEFAULT CURRENT_TIME, "d_maj" DATETIME);
CREATE TABLE "Reservation" ("id_service" INTEGER NOT NULL , "id_up" INTEGER NOT NULL , "d_deb" DATETIME NOT NULL , "d_fin" DATETIME NOT NULL , "nb_pers" INTEGER NOT NULL , "id_prix" DOUBLE NOT NULL , "d_creation" DATETIME NOT NULL  DEFAULT CURRENT_TIME, "d_maj" DATETIME);
CREATE TABLE "Service_Activite" ("id_service" INTEGER NOT NULL , "id_activite" INTEGER NOT NULL , PRIMARY KEY ("id_service", "id_activite"));
CREATE TABLE "Services" ("id_service" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "id_geo" INTEGER, "id_ue" INTEGER, "nom" VARCHAR, "description" TEXT, "d_creation" DATETIME DEFAULT CURRENT_TIME, "d_lastUpdate" DATETIME DEFAULT CURRENT_TIME, "id_adresse" INTEGER);
CREATE TABLE "Theme" ("id_theme" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "nom" VARCHAR NOT NULL  UNIQUE , "description" TEXT);
CREATE TABLE "Theme_Activite" ("id_theme" INTEGER NOT NULL , "id_activite" INTEGER NOT NULL , PRIMARY KEY ("id_theme", "id_activite"));
CREATE TABLE "Utilisateur_Entreprise" ("id_ue" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "id_adresse" INTEGER, "id_contact_e" INTEGER, "id_contact_p" INTEGER, "d_creation" DATETIME DEFAULT CURRENT_TIME);
CREATE TABLE "Utilisateur_Particulier" ("id_up" INTEGER PRIMARY KEY  NOT NULL ,"id_contact_p" INTEGER,"d_creation" DATETIME NOT NULL  DEFAULT (CURRENT_TIME) );
CREATE TABLE "pos_geo" ("id_geo" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "lat" DOUBLE NOT NULL , "lon" DOUBLE NOT NULL , "descrition" TEXT, "d_creation" DATETIME NOT NULL  DEFAULT CURRENT_TIME, "d_maj" DATETIME NOT NULL  DEFAULT CURRENT_TIME, "source" INTEGER);
